import { createSlice } from "@reduxjs/toolkit";

const schoolSlice = createSlice({
  name: "schoolData",
  initialState: {
    siginData: [],
    isLogeddin: false,
  },
  reducers: {
    siginData: (state, action) => {
      state.siginData = action.payload;
    },
    login: (state) => {
      state.isLogeddin = true;
    },
    logOut: (state) => {
      state.isLogeddin = false;
    },
  },
});

export const { login, logOut, siginData } = schoolSlice.actions;
export default schoolSlice.reducer; // Corrected to .reducer
